var play = document.getElementsByClassName('play')[0]
// console.log(play)

var restartButton = document.getElementsByClassName('restart')[0]
// console.log(restartButton)

var credits = 10
var creDiv = document.getElementById('nCredits') // get the span containing credits
// console.log(creDiv)

var debugBox = document.getElementById('debug');
// console.log(debugBox);

var showDebug = document.getElementById('showDebug');
// console.log(showDebug)

debugShowing = false;

showDebug.onclick = function () {
    if (debugShowing) {
        debugShowing = false
        console.log(debugShowing)
        debugBox.style.display = 'none'
        showDebug.innerText = 'SHOW DEBUG'
    }
    else {
        debugShowing = true
        console.log(debugShowing)
        debugBox.style.display = 'flex'
        showDebug.innerText = 'HIDE DEBUG'
    }
}

var fruits = {
    cherry: { points: 10, src: 'https://img.icons8.com/emoji/2x/cherries.png' },
    lemon: { points: 20, src: 'https://img.icons8.com/fluency/2x/citrus.png' },
    orange: { points: 30, src: 'https://img.icons8.com/fluency/2x/orange.png' },
    watermelon: { points: 40, src: 'https://img.icons8.com/cotton/2x/watermelon-2.png' }
}

// https://img.icons8.com/fluency/2x/citrus.png
// https://img.icons8.com/cotton/2x/watermelon-2.png

creDiv.innerText = credits; // populate the credits

var tiles = document.getElementsByClassName('tileImg')
// console.log(tiles);

// function to choose tile images
function choose(choices) {
    var index = Math.floor(Math.random() * choices.length);
    return choices[index];
}

// function to make sound
function beep() {
    var snd = new Audio("data:audio/wav;base64,")
    snd.play()
}


restartButton.onclick = function () {
    credits = 10;
    creDiv.innerText = credits; // populate the credits
    // debugShowing = false
    // console.log(debugShowing)
    // debugBox.style.display = 'none'
    // showDebug.innerText = 'SHOW DEBUG'
    tiles[0].src = ''
    tiles[1].src = ''
    tiles[2].src = ''
    play.innerText = 'Play';
        play.className = 'play';
}

play.onclick = function () {
    if (credits > 1) {
        credits = credits - 1;
        creDiv.innerText = credits; // populate the credits

        let tile1 = choose(Object.entries(fruits)) // choose a fruit for tile 1
        tiles[0].src = tile1[1].src // set the image

        let tile2 = choose(Object.entries(fruits)) // choose a fruit for tile 1
        tiles[1].src = tile2[1].src // set the image

        let tile3 = choose(Object.entries(fruits)) // choose a fruit for tile 1
        tiles[2].src = tile3[1].src // set the image

        if (debugShowing) {
            debugBox.innerHTML = `<span>${tile1[0]}</span><span>${tile2[0]}</span><span>${tile3[0]}</span>`;
        }

        if (tile1[0] == tile2[0] && tile2[0] == tile3[0]) { // check that the images are the same
            beep() // make a beep sound
            credits = credits + tile1[1].points;
            creDiv.innerText = credits; // populate the credits
        }
    }
    else {
        creDiv.innerText = 0; // populate the credits
        play.innerText = 'Bye!';
        play.className = 'invalid';
    }
}